import ReactDOM from 'react-dom';
import React, {Component} from 'react';
import { w3cwebsocket as W3CWebSocket } from 'websocket';
import { Card, Avatar, Input, Typography } from 'antd';
// import 'antd/dist/antd.css';
import './index.css'
const { Search } = Input;
const { Text } = Typography;
const { Meta } = Card;

const  client= new W3CWebSocket('ws://127.0.0.1:8006');

export default class App extends Component {

    state = {
       userName: '',
       isLoggedIn: false,
       messages: [],
    }

    onButtonClicked = (value) => {
        client.send(JSON.stringify({
            type: "message",
            msg: value,
            user: this.state.userName,
        }));
        this.setState({ searchVal: ''})
    }

    componentDidMount() {
        client.onopen = () => {
            console.log('WebSocket Client Connected');
        };

        client.onmessage = (message) => {
            const dataFromServer = JSON.parse(message.data);
            console.log('got reply! ', dataFromServer);
            if (dataFromServer.type === 'message') {
                // this.setState((state) => 
                //     ([
                //         message: [ ... state.messages,
                //         {
                //             msg: dataFromServer.msg,
                //             user: dataFromServer.user
                //         }]
                //     ])
                // );
                const newMessage = {
                    msg: dataFromServer.msg,
                    user: dataFromServer.user
                  };

                  this.setState(prevState => ({
                    messages: [...prevState.messages, newMessage]
                  }));

            }
        };
    }

    render() {
        return (
            <div className='main'>
                <div className='title'>
                    <Text type="secondary" style={{ fontSize: '36px' }}>Websocket Chat</Text>
                </div>
                {this.state.isLoggedIn ?
                <div>
                    <div style={{ display: 'flex', flexDirection: 'column', paddingBottom: 50}} id="messages">
                        {this.state.messages.map(message => 
                            <Card key={message.msg} style={{ width: 300, margin: '16px 4px 0 4px', alignSelf: this.state.userName === message.user ? 'flex-end' : 'flex-start'}} loading={false}>
                                <Meta 
                                    avatar={
                                        <Avatar style={{ color: '#f56a100', backgroundColor: '#fde3cf'}}>{message.user[0].toUpperCase()}</Avatar>
                                    }
                                    title={message.user+""}
                                    description={message.msg}
                                />
                            </Card>
                            )}
                    </div>
                    <div className='bottom'>
                        <Search 
                            placeholder='input message and send'
                            enterButton='Send'
                            value={this.state.searchVal}
                            size='Large'
                            onChange={(e) => this.setState({ searchVal: e.target.value })}
                            onSearch={value => this.onButtonClicked(value)}
                        />
                    </div>
                </div>    
                :
                    <div style={{ padding: "20px 40px" }}>
                        <Search 
                            placeholder='Enter Username'
                            enterButton='Login'
                            size='Large'
                            onSearch={value => this.setState({ isLoggedIn: true, userName: value })}
                        />
                    </div>
                }
            </div>
        )
    }
}

ReactDOM.render(<App />, document.getElementById('root'));
